insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (3, 55, 6, 2, 'https://ibb.co/2kwPS5n', 4, 0, 'В сезон полуниці можна поласувати ароматною свіжою ягідкою, а також приготувати страви з полуницею.', 'Вареники з полуницею');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (2, 110, 6, 5, 'https://ibb.co/SBKp57x', 6, 0, 'Давайте приготуємо дуже простий і швидкий, але неймовірно смачний грибний суп з вершками. В цьому рецепті ми використовували шампіньйони, але можна взяти будь-які інші лісові гриби. Від цього смак супу стане ще кращим', 'Грибний суп');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (3, 45, 6, 4, 'https://ibb.co/xfBWB1n', 8, 0, 'Швидкий пиріг з чорницею справді дуже смачний! Він виходить вологим і чудово ріжеться. Цей простий у приготуванні чорничний пиріг зникає з тарілок в одну мить.', 'Пиріг з чорницею');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (3, 50, 6, 3, 'https://ibb.co/J5vq09B', 12, 1, 'Сьогодні будемо готувати надзвичайно смачні та апетитні фаршировані перці з м’ясом та рисом. Така страва буде ідеальним варіантом вечері, її обожнюють як дорослі так і діти. Готувати такі фаршировані перці зовсім нескладно.', 'Фарширований перець');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (2, 90, 6, 1, 'https://ibb.co/ChF3GxS', 6, 0, 'Розрепанці – це невеликі картопляні пиріжки, скріплені тільки по краях. Найчастіше їх готували з гороховою чи квасолевою начинкою та подавали з “маштаркою” – соусом з горіхів, гарбузового та лляного насіння та сметани, з яким традиційно подають картопляники.', 'Картопляні пиріжки з горохом, або подільські розрепанці');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (1, 45, 6, 2, 'https://ibb.co/fGBK56N', 1, 0, 'Важко уявити зиму без ароматного малинового варення. Воно смачне та неймовірно корисне, бо полегшує стан здоров’я при сезонній застуді. Ми захотіли урізноманітнити класичний смак малинового варення та додали до нього м’якуш апельсина, адже цитрусові добре поєднуються з ягодами.', 'Варення з малини та апельсина');

