insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (2, 180, 3, 3, 'https://ibb.co/QPwTft9', 10, 0, 'Традиційна страва кримськотатарської кухні, яка складається з м\'яса, овочів та тіста, звичайно в формі широких локшино-подібних смужок, варених у бульйоні.', 'Бешбармак');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (2, 60, 3, 5, 'https://ibb.co/rptJ6nM', 6, 0, 'Чорба - це густий суп з м''ясом та овочами, який часто має насичений смак і аромат завдяки спеціям та зелені.', 'Чорба');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (2, 90, 3, 3, 'https://ibb.co/c209gG6', 6, 0, 'Це традиційна страва кримськотатарської кухні, яка складається з м''ясного соусу на основі яловичини або баранини та довгих макаронних виробів.  Це смачна і насичена страва, яка ідеально підходить для холодних осінніх і зимових днів.', 'Лагман');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (2, 60, 3, 5, 'https://ibb.co/TcjWWS4', 6, 1, 'Шурпа - це традиційна страва кримськотатарської кухні, в основі якої лежить бульйон на основі м''яса, овочів та спецій. Вона може бути дуже густою або ж, навпаки, водянистою, в залежності від рецепту і традицій регіону.', 'Шурпа');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (3, 60, 3, 2, 'https://ibb.co/Fh6bgRG', 10, 0, 'Це медові шматочки тіста, які спочатку фритюрюються в олії, а потім заливають медовою сиропом. Вони можуть мати різний розмір та форму, а також прикрашаються маком або іншими насіннями. Чак-чак зазвичай подається на святковій та урочистій татарській вечері разом з іншими національними стравами.', 'Чак-чак');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (3, 40, 3, 4, 'https://ibb.co/hHv35jL', 8, 0, 'Сомси - це популярна кримськотатарська випічка з начинкою. Ось простий рецепт, який можна спробувати приготувати вдома.', 'Сомси');

insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (1, 20, 2, 1, 'https://ibb.co/y0k5PjL', 4, 0, 'Бурячки з хроном або цвіклі – одна із найулюбленіших страв Галицької кухні та на теренах західної України. Зазвичай цю страву подають до холодцю, копченого м’яса.', 'Цвіклі');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (2, 90, 2, 5, 'https://ibb.co/Tg1QJZg', 6, 0, 'Журек - це страва галицької кухні, яка складається з кислого тіста та густого соусу на основі м\'ясного бульйону.', 'Журек');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (2, 120, 2, 3, 'https://ibb.co/bBLCS0Y', 8, 0, 'Чанахи - це чудова м''ясна страва галицької кухні, яка зазвичай готується з баранини або телятини та овочів. М''ясо та овочі смажаться зі спеціями та запікаються в глиняному горщику, що надає страві особливого смаку та аромату.', 'Чанахи');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (2, 120, 2, 5, 'https://ibb.co/CVms9hj', 6, 1, 'Гарний варіант зупи - галицька зупа, яка має насичений смак завдяки домашньому бульйону та різноманітним інгредієнтам. Вона здатна зігріти у холодну пору року, а також надасть відчуття повноцінного обіду.', 'Зупа');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (3, 40, 2, 4, 'https://ibb.co/ZGrX96K', 6, 0, 'Львівський сирник - це смачна галицька випічка, яка має круглу форму, золотисту скоринку і м''яку начинку.Вона зроблена з творогу, яєць, манної крупи, цукру та вершків, і має неповторний смак та аромат.', 'Львівський сирник');

insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (3, 30, 5, 1, 'https://ibb.co/FYQLGxM', 6, 0, 'Форшмак - це традиційна страва єврейської кухні, популярна в Одеському регіоні. Форшмак подається в прохолодному вигляді з чорним хлібом, і має незвичайний, але смачний смак, який відмінно поєднується зі свіжим хлібом.', 'Форшмак');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (1, 20, 5, 1, 'https://ibb.co/HYR7DRt', 6, 0, 'Мідії по-одеськи - це одна з найвідоміших страв одеської кухні, яка здивує вас своєю смаковою насиченістю та ароматом. Це дуже простий рецепт, який з легкістю можна приготувати вдома.', 'Мідії по-Одеськи');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (3, 30, 5, 5, 'https://ibb.co/SsSTp43', 6, 0, 'Рибна юшка - це смачна страва, приготована на основі риби, овочів та спецій. Її основний інгредієнт - свіжа риба. Це відмінний вибір для тих, хто любить рибні страви та шукає простий та смачний рецепт', 'Рибна юшка');
insert into recipes (complexity_id, cooking_time, cuisine_region_id, dish_type_id, image_url, portions, spiced, subtitle, dish_name)
values (3, 60, 5, 4, 'https://ibb.co/FbT16tJ', 6, 0, 'Штрудель з яблуками це ніжні шари тоненької тіста, просочені маслом, заповнені солодкими яблуками, горіхами та спеціями.', 'Штрудель з яблуками');

